mapper.select:
select * from (select c.*, rownum rn from ( SELECT b.xn, b.xq, b.jszgh, j.xm jsxm, b.sksl, b.jxbsl, b.zf FROM (SELECT t.xn, t.xq, t.jszgh, COUNT(DISTINCT t.kcdm) sksl, COUNT(t.jxb) jxbsl, round(AVG(t.zf), 2) zf FROM view_tjfx_pjjg t GROUP BY t.jszgh, t.xn, t.xq) b LEFT JOIN jxpj_jcsj_jsxx j ON b.jszgh = j.zgh
) c )
where rn >= #{startRow} and rn <= #{endRow}

select * from (select c.*, rownum rn from ( SELECT b.xn, b.xq, b.kcdm, k.kczwmc kcmc, b.skjssl, b.jxbsl, b.zf FROM (SELECT t.xn, t.xq, t.kcdm, COUNT(DISTINCT jszgh) skjssl, COUNT(jxb) jxbsl, round(AVG(t.zf), 2) zf FROM view_tjfx_pjjg t GROUP BY t.kcdm, t.xn, t.xq) b LEFT JOIN jxpj_jcsj_kcdmb k ON b.kcdm = k.kcdm
) c )
where rn >= #{startRow} and rn <= #{endRow}

