mapper.select:
    	SELECT t.kcmc || '(' || t.kcdm || ')' kcmc,
		       t.jsxm || '(' || t.jszgh || ')' jsxm,
		       '教学' || t.jxb || '班(' || t.jsxm ||')' jxbjs,
		       '教学' || t.jxb || '班(' || t.kcmc ||')' jxbkc,
		       t.zf
		  FROM view_tjfx_pjjg t

        	1=1

            and t.xn = #{xn}


            and t.xq = #{xq}


            and t.kcdm = #{kcdm}


            and t.jszgh = #{jszgh}

    	SELECT t.jszgh,
		       (SELECT COUNT(1) FROM jxpj_sjcd_xspfjgb t WHERE 1=1 

	            and t.kcdm = #{kcdm}


	            and t.jszgh = #{jszgh}

		       ) pjzs,
		       COUNT(1) yxsl,
		       round(STDDEV(t.zf), 2) fc,
		       MAX(zf) zdz,
		       MIN(zf) zxz
		  FROM jxpj_sjcd_xspfjgb t

        	1=1

            and t.xn = #{xn}


            and t.xq = #{xq}


            and t.kcdm = #{kcdm}


            and t.jszgh = #{jszgh}


            ${condition}


        GROUP BY t.jszgh

    	SELECT COUNT(1)
		  FROM jxpj_sjcd_xspfjgb t

        	1=1

            and t.xn = #{xn}


            and t.xq = #{xq}


            and t.kcdm = #{kcdm}


            and t.jszgh = #{jszgh}


	        ${condition}

SELECT xm mc, t.pjdf, '[' || yjzbmc || ']' || pjnr pjnr FROM jxpj_sjqx_jsdxdf t WHERE jszgh = #{jszgh} AND pjh = #{pjh} AND xkkh = #{xkkh} AND kcdm = #{kcdm}
UNION SELECT kkxy mc, pjdf, '[' || yjzbmc || ']' || pjnr pjnr FROM jxpj_sjqx_xydxdf WHERE pjh = #{pjh} AND kcdm = #{kcdm}
UNION SELECT '全校' mc, pjdf, '[' || yjzbmc || ']' || pjnr pjnr FROM jxpj_sjqx_qxdxdf WHERE pjh = #{pjh} AND kcdm = #{kcdm} AND rownum = 1

SELECT xm mc, t.pjdf, '[' || yjzbmc || ']' || pjnr pjnr FROM jxpj_sjqx_kc_jsdxdf t WHERE jszgh = #{jszgh} AND pjh = #{pjh} AND kcdm = #{kcdm}
UNION SELECT kkxy mc, pjdf, '[' || yjzbmc || ']' || pjnr pjnr FROM jxpj_sjqx_kc_xydxdf t1 LEFT JOIN jxpj_jwsj_pjzbflb p ON t1.zbfl = p.fldm WHERE pjh = #{pjh} AND p.flmc = #{zbfl}
UNION SELECT '全校' mc, pjdf, '[' || yjzbmc || ']' || pjnr pjnr FROM jxpj_sjqx_kc_qxdxdf t2 LEFT JOIN jxpj_jwsj_pjzbflb p ON t2.zbfl = p.fldm WHERE pjh = #{pjh} AND p.flmc = #{zbfl} AND rownum = 1

select * from (select a.*, rownum rn from ( SELECT t.xn, t.xq, t.kcdm, t.kcmc, t.kkxy yx, t.xkkh, t.jszgh, t.xm jsxm, t.yjzbmc, p.flmc zbfl, t.pjh, t.pjnr, t.pjdf FROM jxpj_sjqx_jsdxdf t LEFT JOIN jxpj_jwsj_pjzbflb p ON t.zbfl = p.fldm
ORDER BY t.pjdf desc ) a )
where rn >= #{startRow} and rn <= #{endRow}

select * from (select a.*, rownum rn from ( SELECT t.xn, t.xq, t.kcdm, t.kcmc, t.kkxy yx, t.jszgh, t.xm jsxm, t.yjzbmc, p.flmc zbfl, t.pjh, t.pjnr, t.pjdf FROM jxpj_sjqx_kc_jsdxdf t LEFT JOIN jxpj_jwsj_pjzbflb p ON t.zbfl = p.fldm
ORDER BY t.pjdf desc ) a )
where rn >= #{startRow} and rn <= #{endRow}

