SELECT c.kkxy, count(1) as ycqrs, sum(CASE WHEN c.kqqk = '0' THEN 1 ELSE 0 END ) as sjcqrs, sum(CASE WHEN c.kqqk != '0' THEN 1 ELSE 0 END ) as qqrs, round(sum(CASE when c.kqqk = '0' THEN 1 ELSE 0 END)/count(1),3)*100 cqbl, round(sum(CASE when c.kqqk != '0' THEN 1 ELSE 0 END)/count(1),3)*100 qqbl, sum(CASE when c.kqqk = '0' THEN (select 1 from jxpj_sspj_xspj t1 where t1.pjid = c.pjid) ELSE 0 END) as pjcyrs FROM (SELECT k.kkxy, t.* FROM jxpj_sspj_skkqglb t, (SELECT w.kkxy, w.xkkh FROM jxpj_jcsj_jxrwb w GROUP BY w.xkkh, w.kkxy) k
t.kcid = k.XKKH
and k.kkxy=#{dept}
and j.globalid=#{globalid}
and substr(t.sksj, 1, 4) = #{year}
and to_date(t.sksj, 'yyyy-MM-dd') between to_date(#{firstDay}, 'yyyy-MM-dd') and to_date(#{lastDay}, 'yyyy-MM-dd')
