and to_date(t.sksj, 'yyyy-MM-dd') between to_date(#{kcsjFrom}, 'yyyy-MM-dd') and to_date('9999-12-31', 'yyyy-MM-dd')
) c group by c.kkxy order by sum(CASE when c.kqqk = '0' THEN 1 ELSE 0 END)/count(1) DESC

