SELECT a.wjid, a.xwjid, a.wjmc, a.globalid FROM (SELECT DISTINCT t.wjid, t.xwjid, p.wjmc, kb.globalid, kb.kcsj FROM jxpj_jcsj_kcbxx kb, jxpj_jbpz_pjgxb t, jxpj_jbpz_wjpzb p, jxpj_sspj_skkqglb t1, jxpj_sspj_xspj t2 WHERE kb.globalid = t1.globalid AND t1.pjid = t2.pjid AND t2.pjid = t.pjid AND t.wjid = p.wjid AND p.djrylx = t.pjrylx
